import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

export const fetchListsForUser = createAsyncThunk(
    'lists/fetchForUser',
    async (userId,thunkAPI)=>{
        const response = await fetch(`/users/${userId}/lists`)
        const lists = await response.json()
        return lists
    }
)

export const listsSlice = createSlice({
    name:'lists',
    initialState:{
        lists:[],
        status:'idle',
        error:null,
        selectedListId:null,
    },
    reducers:{
        setLists:(state,action)=>{
            state.lists=action.payload;
        },
        selectList: (state,action)=>{
            state.selectedListId=action.payload
        }
    },
    extraReducers: (builder) => { // Fix the case and use builder callback notation
        builder
          .addCase(fetchListsForUser.fulfilled, (state, action) => {
            state.status = 'succeeded';
            state.lists = action.payload;
          });
      },
    });

export const {setLists,selectList} = listsSlice.actions;
export default listsSlice.reducer;
