// contactsSlice.js
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

const initialState = {
  contacts: [],
  status: 'idle',
  error: null,
};

export const addContact = createAsyncThunk('contacts/addContact',async(contactData,{rejectWithValue})=>{
  try{
    const response = await fetch('/contacts',{
      method:'POST',
      headers:{
        'Content-Type':'application/json',
      },
      body:JSON.stringify(contactData)
    });
    if (!response.ok){
      throw new Error('Network response was not ok')
    }
    return response.json();
  } catch (error){
    return rejectWithValue(error.message)
  }
});


export const fetchAllContacts = createAsyncThunk('contacts/fetchAllContacts', async (userId) => {
  const url = `/contacts-lists/${userId}`; 
  const response = await fetch(url);

  if (!response.ok) {
    console.error(`HTTP error! status: ${response.status}`);
    throw new Error('Could not fetch contacts');
  }

  const data = await response.json();
  return data;
});

export const addList = createAsyncThunk(
  'lists/addList',
  async (listData, { rejectWithValue }) => {
    try {
      const response = await fetch('/lists', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(listData),
      });
      if (!response.ok) {
        throw new Error('Failed to add list');
      }
      const data = await response.json();
      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);


export const contactsSlice = createSlice({
  name: 'contacts',
  initialState,
  reducers: {
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchAllContacts.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchAllContacts.fulfilled, (state, action) => {
        state.status = 'succeeded';
        // Set the contacts to the fetched data
        state.contacts = action.payload;
      })
      .addCase(fetchAllContacts.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(addContact.pending,(state)=>{
        state.status='loading';
      })
      .addCase(addContact.fulfilled,(state,action)=>{
        state.status='succeeded';
        state.contacts.push(action.payload)
      })
      .addCase(addContact.rejected,(state,action)=>{
        state.status='failed';
        state.error=action.error.message;
      })
      .addCase(addContact.fulfilled,(state,action)=>{
        state.status='succeeded';
        state.contacts.push(action.payload)
      })
  },
});

export default contactsSlice.reducer;
