import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addTodo } from './slices/listsSlice'; // Adjust the import path as necessary

const NewTodoForm = ({ listId }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [completed, setCompleted] = useState(false);
  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ensure listId is provided correctly
    dispatch(addTodo({
      title,
      description,
      list_id: listId, // Ensure this prop is passed correctly to the form
      completed,
      due_date: dueDate ? new Date(dueDate).toISOString() : null, // Convert to ISO string if dueDate is provided
    }));
    // Reset form fields
    setTitle('');
    setDescription('');
    setDueDate('');
    setCompleted(false);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Todo title"
        required
      />
      <textarea
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        placeholder="Todo description"
      />
      <input
        type="date"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
        placeholder="Due date"
      />
      <label>
        Completed:
        <input
          type="checkbox"
          checked={completed}
          onChange={(e) => setCompleted(e.target.checked)}
        />
      </label>
      <button type="submit">Add Todo</button>
    </form>
  );
};

export default NewTodoForm;
