import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { updateContact, deleteContact } from './slices/contactsSlice';

const ManageContacts = () => {
  const contacts = useSelector((state) => state.contacts.contacts);
  console.log('this is contacts', contacts)
  const companies = useSelector(state=>state.companies.companies)
  console.log('this is companies', companies)
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [editRowId, setEditRowId] = useState(null);
  const [selectedCompanyId,setSelectedCompanyId] = useState('')

  const contactSchema = Yup.object().shape({
    name: Yup.string()
      .required('Name is required')
      .min(2, 'Name must be at least 2 characters')
      .max(50, 'Name must not exceed 50 characters'),
    status: Yup.string()
      .required('Status is required'),
  });

  const handleBack = () => {
    navigate('/');
  };

  const filteredContacts = selectedCompanyId
  ? contacts.filter(contact => contact.company_id?.toString() === selectedCompanyId)
  : contacts;

  const handleAddContactClick = () => {
    navigate('/add-contact');
  };

  const handleCancelClick = () => {
    setEditRowId(null);
  };

  const handleCompanyChange = (event)=>{
    setSelectedCompanyId(event.target.value)
  }

  const handleDeleteClick = (contactId) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      dispatch(deleteContact(contactId))
        .catch((error) => {
          console.error('Delete failed', error);
        });
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Manage Contacts</h1>
      {/* Button and select components omitted for brevity */}
      <div className="overflow-x-auto">
        <table className="table w-full">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Status</th>
              <th>Job Title</th> {/* New column */}
              <th>Phone</th> {/* New column */}
              <th>Email</th> {/* New column */}
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
          {contacts.map((contact) => (
              <tr key={contact.id}>
                <td>{contact.id}</td>
                <td>
                  {editRowId === contact.id ? (
                    <Field name="name" type="text" />
                  ) : (
                    contact.name
                  )}
                </td>
                <td>
                  {editRowId === contact.id ? (
                    <Field as="select" name="status">
                      {/* Status options */}
                    </Field>
                  ) : (
                    contact.status
                  )}
                </td>
                <td>
                  {editRowId === contact.id ? (
                    <Field name="job_title" type="text" />
                  ) : (
                    contact.job_title
                  )}
                </td>
                <td>
                  {editRowId === contact.id ? (
                    <Field name="phone" type="text" />
                  ) : (
                    contact.phone
                  )}
                </td>
                <td>
                  {editRowId === contact.id ? (
                    <Field name="email" type="email" />
                  ) : (
                    contact.email
                  )}
                </td>
                <td>
                  {editRowId === contact.id ? (
                    <Formik
                      initialValues={{
                        name: contact.name,
                        status: contact.status,
                        job_title: contact.job_title,
                        phone: contact.phone,
                        email: contact.email,
                      }}
                      validationSchema={contactSchema}
                      onSubmit={(values) => {
                        dispatch(updateContact({ id: contact.id, ...values }));
                        setEditRowId(null);
                      }}
                    >
                      {({ isSubmitting }) => (
                        <Form>
                          <button type="submit" disabled={isSubmitting}>
                            Save
                          </button>
                          <button type="button" onClick={() => setEditRowId(null)}>
                            Cancel
                          </button>
                        </Form>
                      )}
                    </Formik>
                  ) : (
                    <>
                      <button onClick={() => setEditRowId(contact.id)}>Edit</button>
                      <button onClick={() => handleDeleteClick(contact.id)}>Delete</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageContacts;
