import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Breadcrumbs = () => {
  const location = useLocation();

  const displayNameMapping = {
    'manage-companies': 'Manage Companies',
    'add-company': 'Add Company',
    // Add more mappings as needed
  };

  const crumbs = location.pathname.split('/').filter(crumb => crumb !== '').map((crumb, index, array) => {
    const currentLink = `/${array.slice(0, index + 1).join('/')}`;
    const displayName = displayNameMapping[crumb] || crumb;

    return (
      <div className="crumb" key={crumb}>
        <Link to={currentLink}>{displayName}</Link>
        {index < array.length - 1 && ' / '}
      </div>
    )
  })

  return (
    <div className="breadcrumbs">
      {crumbs}
    </div>
  );
};

export default Breadcrumbs;
