import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Breadcrumbs = () => {
  const location = useLocation();
  console.log(location);
  let currentLink = '';

  // Fixes the breadcrumb link accumulation
  const crumbs = location.pathname.split('/').filter(crumb => crumb !== '').reduce((acc, crumb) => {
    currentLink += `/${crumb}`;
    acc.push(
      <div className="crumb" key={currentLink}> {/* Use currentLink as key for uniqueness */}
        <Link to={currentLink}>{crumb.replace(/-/g, ' ')}</Link> {/* Replace hyphens with spaces */}
      </div>
    );
    return acc;
  }, []);

  // Only show breadcrumbs if there is more than one crumb
  if (crumbs.length <= 1) return null;

  return (
    <nav className="breadcrumbs">
      {crumbs}
    </nav>
  );
};

export default Breadcrumbs;
