import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { updateContact, deleteContact } from './slices/contactsSlice';

const ManageContacts = () => {
  const contacts = useSelector((state) => state.contacts.contacts);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Function to navigate to the edit contact page, passing the contact's ID
  const handleEditClick = (contactId) => {
    navigate(`/edit-contact/${contactId}`);
  };

  const handleDeleteClick = (contactId) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      dispatch(deleteContact(contactId))
        .unwrap()
        .then(() => {
          alert('Contact deleted successfully');
        })
        .catch((error) => {
          alert(`Delete failed: ${error.message}`);
        });
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Manage Contacts</h1>
      <button onClick={() => navigate('/add-contact')} className="btn btn-primary mb-4">Add New Contact</button>
      <div className="overflow-x-auto">
        <table className="table w-full">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Status</th>
              <th>Job Title</th> {/* Displaying new field */}
              <th>Phone</th> {/* Displaying new field */}
              <th>Email</th> {/* Displaying new field */}
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact) => (
              <tr key={contact.id}>
                <td>{contact.id}</td>
                <td>{contact.name}</td>
                <td>{contact.status}</td>
                <td>{contact.job_title || 'N/A'}</td> {/* Display job title */}
                <td>{contact.phone || 'N/A'}</td> {/* Display phone */}
                <td>{contact.email || 'N/A'}</td> {/* Display email */}
                <td>
                  <div className="flex gap-2">
                    <button onClick={() => handleEditClick(contact.id)} className="btn btn-info">Edit</button>
                    <button onClick={() => handleDeleteClick(contact.id)} className="btn btn-error">Delete</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageContacts;
