import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { deleteCompanyContacts } from './slices/contactsSlice';
import { updateCompany, deleteCompany } from './slices/companiesSlice';

// Validation schema for company form
const companySchema = Yup.object().shape({
  name: Yup.string()
    .required('Name is required')
    .min(2, 'Name must be at least 2 characters')
    .max(50, 'Name must not exceed 50 characters'),
});

// Company Row component for editing and viewing company details
const CompanyRow = ({ company, editRowId, setEditRowId, dispatch }) => {
  const handleEditSave = (values, { setSubmitting }) => {
    dispatch(updateCompany({ ...company, name: values.name }))
      .catch((error) => console.error('Update failed', error));
    setEditRowId(null);
    setSubmitting(false);
  };

  if (editRowId === company.id) {
    return (
      <Formik
        initialValues={{ name: company.name }}
        validationSchema={companySchema}
        onSubmit={handleEditSave}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field name="name" type="text" className="input input-bordered w-full max-w-xs" />
            <ErrorMessage name="name" component="div" className="text-error" />
            <div className="flex justify-end mt-2">
              <button type="submit" disabled={isSubmitting} className="btn btn-success">Save</button>
              <button type="button" onClick={() => setEditRowId(null)} className="btn btn-ghost">Cancel</button>
            </div>
          </Form>
        )}
      </Formik>
    );
  }

  return (
    <>
      <td className="text-3xl">{company.id}</td>
      <td className="text-3xl">{company.name}</td>
      <td className="text-3xl">
        {company.image_path ? (
          <img src={company.image_path} alt={`${company.name} logo`} className="company-logo" />
        ) : (
          'No Image'
        )}
      </td>
      <td>
        <button onClick={() => setEditRowId(company.id)} className="btn btn-accent btn-lg">Edit</button>
        <button onClick={() => confirm('Are you sure you want to delete this company?') && dispatch(deleteCompany(company.id)) && dispatch(deleteCompanyContacts(company.id))} className="btn btn-error">Delete</button>
      </td>
    </>
  );
};

const ManageCompanies = () => {
  const companies = useSelector((state) => state.companies.companies);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [editRowId, setEditRowId] = useState(null);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-6xl font-bold mb-4 text-center">Manage Companies</h1>
      <div className="flex justify-between mb-4">
        <button onClick={() => navigate('/add-company')} className="btn btn-primary btn-lg">Add New Company</button>
        <button onClick={() => navigate('/')} className="btn px-10 py-3 text-lg">Back to Home</button>
      </div>
      <div className="overflow-x-auto">
        <table className="table w-full">
          <thead>
            <tr>
              <th className="text-5xl">ID</th>
              <th className="text-5xl">Name</th>
              <th className="text-5xl">Image</th>
              <th className="text-5xl">Actions</th>
            </tr>
          </thead>
          <tbody>
            {companies.map((company) => (
              <tr key={company.id}>
                <CompanyRow
                  company={company}
                  editRowId={editRowId}
                  setEditRowId={setEditRowId}
                  dispatch={dispatch}
                />
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageCompanies;
