import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchListsForContact } from './slices/listsSlice';

const ContactListsSidebar = ({ selectedContactId, onListSelect }) => {
  const dispatch = useDispatch();
  const lists = useSelector((state) => state.lists.lists);

  useEffect(() => {
    if (selectedContactId) {
      dispatch(fetchListsForContact(selectedContactId));
    }
  }, [selectedContactId, dispatch]);

  return (
    <div>
      {lists.map((list) => (
        <div key={list.id} onClick={() => onListSelect(list.id)}>
          {list.name}
        </div>
      ))}
    </div>
  );
};

export default ContactListsSidebar;
