import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import '../styles/ManageCompanies.css';
import { updateCompany, deleteCompany } from './slices/companiesSlice';

const ManageCompanies = () => {
  const companies = useSelector((state) => state.companies.companies);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [editRowId, setEditRowId] = useState(null);
  const [editFormData, setEditFormData] = useState({ name: '', id: null });

  const handleBack = () => {
    navigate('/');
  };

  const handleAddCompanyClick = () => {
    navigate('/add-company');
  };

  const handleAddUserClick = () => {
    navigate('/add-user');
  };

  const handleEditClick = (company, e) => {
    e.preventDefault();
    setEditRowId(company.id);
    setEditFormData({ name: company.name, id: company.id });
  };

  const handleCancelClick = () => {
    setEditRowId(null);
  };

  const handleSaveClick = () => {
    dispatch(updateCompany(editFormData))
      .catch((error) => {
        console.error('Update failed', error);
      });
    setEditRowId(null);
  };

  const handleFormChange = (e) => {
    setEditFormData({ ...editFormData, [e.target.name]: e.target.value });
  };

  const handleDeleteClick = (companyId) => {
    if (window.confirm('Are you sure you want to delete this company?')) {
      dispatch(deleteCompany(companyId))
        .catch((error) => {
          console.error('Delete failed', error);
        });
    }
  };

  return (
    <div className="container">
      <button onClick={handleAddCompanyClick}>Add New Company</button>
      <button onClick={handleAddUserClick}>Add New User</button>
      <button onClick={handleBack}>Back to Home</button>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Manager ID</th>
            <th>Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {companies.map((company) => (
            <tr key={company.id}>
              <td>{company.id}</td>
              <td>{company.manager_id}</td>
              <td>
                {editRowId === company.id ? (
                  <input
                    type="text"
                    name="name"
                    value={editFormData.name}
                    onChange={handleFormChange}
                  />
                ) : (
                  <span>{company.name}</span>
                )}
              </td>
              <td>
                {editRowId === company.id ? (
                  <div>
                    <button onClick={handleSaveClick}>Save</button>
                    <button onClick={handleCancelClick}>Cancel</button>
                    <button onClick={() => handleDeleteClick(company.id)}>Delete</button>
                  </div>
                ) : (
                  <button onClick={(e) => handleEditClick(company, e)}>Edit</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageCompanies;
