import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchListsForContact } from './slices/listsSlice';

const ListsDisplay = ({ selectedContactId }) => {
  //pass an additional prop 
  const dispatch = useDispatch();
  const lists = useSelector((state) =>
  //filter lists based on selectedContactId
  state.lists.filter((list) => list.contactId === selectedContactId)
);





  console.log('Lists to display:', lists); // Log the lists to be displayed
  const status = useSelector((state) => state.lists.status);
  const error = useSelector((state) => state.lists.error);

  useEffect(() => {
    if (selectedContactId) {
      dispatch(fetchListsForContact(selectedContactId));
    }
  }, [dispatch, selectedContactId]);


  return (
    <div>
      {lists.map((list) => (
        <div key={list.id}>{list.title}</div>
      ))}
    </div>
  );
};

export default ListsDisplay;
