import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchTodosForContact } from './slices/listsSlice';

const TodosDisplay = ({ selectedContactId }) => {
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.lists.todosByContact[selectedContactId] || []);

  useEffect(() => {
    if (selectedContactId) {
      dispatch(fetchTodosForContact(selectedContactId));
    }
  }, [selectedContactId, dispatch]);

  return (
    <div>
      {todos.map((todo) => (
        <div key={todo.id}>{todo.title}</div>
      ))}
    </div>
  );
};

export default TodosDisplay;
