import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchAllListsAndTodos } from './slices/listsSlice';

const ManageToDo = () => {
  const dispatch = useDispatch();
  const lists = useSelector((state) => state.lists.lists);

  // Fetch all lists and todos when the component mounts
  useEffect(() => {
    dispatch(fetchAllListsAndTodos());
  }, [dispatch]);

  return (
    <div>
      {/* Render lists and todos */}
      <div>
        {lists.map((list) => (
          <div key={list.id}>
            <h3>{list.title}</h3>
            <ul>
              {list.todos.map((todo) => (
                <li key={todo.id}>{todo.title}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageToDo;
