import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { useNavigate, Link } from 'react-router-dom';
import { useDispatch } from 'react-redux'
import { loginUser } from './slices/authSlice';


const LoginForm = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const initialValues = {
    username: '',
    password: '',
  };

  const validationSchema = Yup.object().shape({
    username: Yup.string().required('Username is required'),
    password: Yup.string().required('Password is required'),
  });

  const onSubmit = (values, { setSubmitting, setErrors }) => {
    dispatch(loginUser(values))
      .unwrap()
      .then((user) => {
        console.log('Login Success:', user);
        navigate('/'); 
      })
      .catch((error) => {
        console.error('Error:', error);
        setErrors({ submit: error.message || 'Login failed. Please check your username and password.' });
      })
      .finally(() => setSubmitting(false));
  };

  return (
    <div className="card bg-base-100 shadow-xl p-5">
      <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
        {({ isSubmitting, errors }) => (
          <Form className="card-body">
            <div className="form-control">
              <label htmlFor="username" className="label">
                <span className="label-text">Username</span>
              </label>
              <Field id="username" name="username" placeholder="Username" className="input input-bordered" />
              <ErrorMessage name="username" component="div" className="text-error" />
            </div>

            <div className="form-control">
              <label htmlFor="password" className="label">
                <span className="label-text">Password</span>
              </label>
              <Field id="password" name="password" type="password" placeholder="Password" className="input input-bordered" />
              <ErrorMessage name="password" component="div" className="text-error" />
            </div>

            {errors.submit && <div className="text-error mt-2">{errors.submit}</div>}

            <div className="form-control mt-4">
              <button type="submit" disabled={isSubmitting} className="btn btn-primary">
                Login
              </button>
            </div>
          </Form>
        )}
      </Formik>
      <div className="text-center mt-4">
        Don't have an account? <Link to="/signup" className="link link-primary">Sign up</Link>
      </div>
    </div>
  );
};

export default LoginForm;
