import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchCompanies } from './slices/companiesSlice';
import { fetchContacts } from './slices/contactsSlice';
import { fetchListsAndTodosForContact } from './slices/listsSlice'; // Updated import

const ManageToDo = () => {
  const dispatch = useDispatch();
  const [selectedCompanyId, setSelectedCompanyId] = useState('');
  const [selectedContactId, setSelectedContactId] = useState('');
  const companies = useSelector((state) => state.companies.companies);
  const contacts = useSelector((state) => state.contacts.contacts);
  const lists = useSelector((state) => state.lists.lists);

  useEffect(() => {
    dispatch(fetchCompanies());
  }, [dispatch]);

  useEffect(() => {
    if (selectedCompanyId) {
      dispatch(fetchContacts(selectedCompanyId));
    }
  }, [selectedCompanyId, dispatch]);

  useEffect(() => {
    if (selectedContactId) {
      dispatch(fetchListsAndTodosForContact(selectedContactId)); // Updated dispatch
    }
  }, [selectedContactId, dispatch]);

  const handleCompanyChange = (e) => {
    setSelectedCompanyId(e.target.value);
    setSelectedContactId('');
  };

  const handleContactChange = (e) => {
    setSelectedContactId(e.target.value);
  };

  // Additional code to render the lists and todos will go here

  return (
    <div>
      <select onChange={handleCompanyChange} value={selectedCompanyId}>
        <option value="">Select Company</option>
        {companies.map((company) => (
          <option key={company.id} value={company.id}>
            {company.name}
          </option>
        ))}
      </select>
      {selectedCompanyId && (
        <select onChange={handleContactChange} value={selectedContactId}>
          <option value="">Select Contact</option>
          {contacts.map((contact) => (
            <option key={contact.id} value={contact.id}>
              {contact.name}
            </option>
          ))}
        </select>
      )}

      {/* This is where you'll map over the lists and render their todos */}
      <div>
        {lists.map((list) => (
          <div key={list.id}>
            <h3>{list.title}</h3>
            <ul>
              {list.todos.map((todo) => (
                <li key={todo.id}>{todo.title}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageToDo;
