// contactsSlice.js
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

const initialState = {
  contacts: [],
  status: 'idle',
  error: null,
};

// Async thunk for fetching contacts
export const fetchContacts = createAsyncThunk('contacts/fetchContacts', async (companyId) => {
  const response = await fetch(`/contacts/${companyId}`);
  const data = await response.json();
  if (!response.ok) {
    throw new Error(data.message || 'Could not fetch contacts');
  }
  return data;
});

export const contactsSlice = createSlice({
  name: 'contacts',
  initialState,
  reducers: {
    // Reducers for any synchronous actions
  },
  extraReducers: {
    [fetchContacts.pending]: (state) => {
      state.status = 'loading';
    },
    [fetchContacts.fulfilled]: (state, action) => {
      state.status = 'succeeded';
      state.contacts = action.payload;
    },
    [fetchContacts.rejected]: (state, action) => {
      state.status = 'failed';
      state.error = action.error.message;
    },
  },
});

export default contactsSlice.reducer;
