import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchListsForContact } from './slices/listsSlice';

const ListsDisplay = ({ selectedContactId }) => {
  const dispatch = useDispatch();
  const lists = useSelector((state) => {
    console.log(`Selecting lists for contact ID ${selectedContactId} from state`);
    return state.lists.listsByContact[selectedContactId] || [];
  })

  console.log('Lists to display:', lists); // Log the lists to be displayed

  const status = useSelector((state) => state.lists.status);
  const error = useSelector((state) => state.lists.error);

  useEffect(() => {
    if (selectedContactId) {
      console.log(`Dispatching action to fetch lists for contact ID ${selectedContactId}`);
      dispatch(fetchListsForContact(selectedContactId));
    }
  }, [selectedContactId, dispatch]);

  if (status === 'loading') return <div>Loading...</div>;
  // Modify this part to display the error message from the state
  if (status === 'failed') return <div>Error: {error}</div>;

  return (
    <div>
      {lists.map((list) => (
        <div key={list.id}>{list.title}</div>
      ))}
    </div>
  );
};

export default ListsDisplay;
