import {createSlice,createAsyncThunk} from '@reduxjs/toolkit'

export const fetchToDosForList = createAsyncThunk(
    'todos/fetchTodos',
    async (listId,thunkAPI) =>{
        const response = await fetch('/api/todos/list/${listId}')
        const todos = await response.json();
        return todos;
    }
)

export const todosSlice = createSlice({
    name: 'todos',
    initialState: {
        items: [],
        status: 'idle',
        error: null,
    },
    extraReducers: (builder) => {
        builder.addCase(fetchToDosForList.pending, (state) => {
            state.status = 'loading';
        })
            .addCase(fetchToDosForList.fulfilled, (state, action) => {
                state.status = 'succeeded'
                state.items = action.payload;
            })
            .addCase(fetchToDosForList.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })
    },
})

export default todosSlice.reducer;
