// ListsDisplay.js
import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchListsForContact } from './slices/listsSlice';
import './ManageToDo.css';

const ListsDisplay = ({ selectedContactId }) => {
  const dispatch = useDispatch();
  const contacts = useSelector((state) => state.contacts.contacts);

  // Find the selected contact
  let selectedContact = contacts.find(contact => contact.id === selectedContactId);

  // Get the lists for the selected contact, if any
  let lists = selectedContact ? selectedContact.lists : [];

  // Fetch lists for the selected contact when it changes
  useEffect(() => {
    if (selectedContactId) {
      dispatch(fetchListsForContact(selectedContactId));
    }
  }, [dispatch, selectedContactId]);

  return (
    <div>
      {lists.map((list) => (
        <div key={list.id}>
          <h3>{list.title}</h3>
          {list.todos.map((todo) => (
            <div key={todo.id}>
              <p>{todo.title}</p>
              <p>{todo.description}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default ListsDisplay;
