import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Breadcrumbs = () => {
  const location = useLocation();

  const displayNameMapping = {
    'manage-companies': 'Manage Companies',
    'add-company': 'Add Company',
    // Add more mappings as needed
  };

  const crumbs = location.pathname.split('/').filter(crumb => crumb !== '').map((crumb, index, array) => {
    const currentLink = `/${array.slice(0, index + 1).join('/')}`;
    const pathnames = location.pathname.split('/').filter(x => x);

    // Log the current path and its parent path
    const parentPath = `/${array.slice(0, index).join('/')}`;
    console.log(`Current path: ${currentLink}, Parent path: ${parentPath}`);

    return (
      <div className="breadcrumbs">
      <Link to="/">Home</Link> {/* Always start from Home */}
      {pathnames.map((value, index) => {
        const last = index === pathnames.length - 1;
        const to = `/${pathnames.slice(0, index + 1).join('/')}`;

        return last ? (
          <span key={index}> {value} </span>
        ) : (
          <Link key={index} to={to}> {value} </Link>
        );
      })}
    </div>
    )
  })

  return (
    <div className="breadcrumbs">
      {crumbs}
    </div>
  );
};

export default Breadcrumbs;
