import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import {updateCompany } from './slices/companiesSlice';

const ManageCompanies = () => {
  const companies = useSelector((state) => state.companies.companies);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [editRowId, setEditRowId] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  const companySchema = Yup.object().shape({
    name: Yup.string()
      .required('Name is required')
      .min(2, 'Name must be at least 2 characters')
      .max(50, 'Name must not exceed 50 characters'),
  });

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSaveChanges = (values) => {
    const formData = new FormData();
    formData.append('name', values.name);
    if (selectedFile) {
      formData.append('image', selectedFile);
    }

    dispatch(updateCompany({id: editRowId, formData})); // Assuming updateCompany can handle FormData
    setEditRowId(null); // Reset edit state
    setSelectedFile(null); // Reset file input
  };

  const handleBack = () => navigate('/');

  const handleAddCompanyClick = () => navigate('/add-company');

  const handleCancelClick = () => {
    setEditRowId(null);
    setSelectedFile(null); // Reset file input on cancel
  };

  const handleDeleteClick = (companyId) => {
    if (window.confirm('Are you sure you want to delete this company?')) {
      dispatch(deleteCompany(companyId));
      dispatch(deleteCompanyContacts(companyId));
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-6xl font-bold mb-4 text-center">Manage Companies</h1>
      <div className="flex justify-between mb-4">
        <button onClick={handleAddCompanyClick} className="btn btn-primary btn-lg">Add New Company</button>
        <button onClick={handleBack} className="btn px-10 py-3 text-lg">Back to Home</button>
      </div>
      <div className="overflow-x-auto">
        <table className="table w-full">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Image</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {companies.map((company) => (
              <tr key={company.id}>
                <td>{company.id}</td>
                <td>
                  {editRowId === company.id ? (
                    <Formik
                      initialValues={{ name: company.name }}
                      validationSchema={companySchema}
                      onSubmit={handleSaveChanges}
                    >
                      <Form>
                        <Field name="name" type="text" className="input input-bordered w-full max-w-xs" />
                        <ErrorMessage name="name" component="div" className="text-error" />
                        <input type="file" onChange={handleFileChange} className="my-2" />
                        <div className="flex justify-end mt-2">
                          <button type="submit" className="btn btn-success">Save</button>
                          <button type="button" onClick={() => handleCancelClick()} className="btn btn-ghost">Cancel</button>
                        </div>
                      </Form>
                    </Formik>
                  ) : (
                    company.name
                  )}
                </td>
                <td>
                  {company.image_path ? (
                    <img src={company.image_path} alt="Company Logo" style={{ width: '100px', height: 'auto' }} />
                  ) : 'No Image'}
                </td>
                <td>
                  <button onClick={() => setEditRowId(company.id)} className="btn btn-info btn-lg">Edit</button>
                  <button onClick={() => handleDeleteClick(company.id)} className="btn btn-error">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageCompanies;
