import React, { useState } from 'react';
import LoginForm from './LoginForm';
import NewUserForm from './NewUserForm';

const LandingPage = () => {
  const [isLoginOpen, setLoginOpen] = useState(false);
  const [isSignUpOpen, setSignUpOpen] = useState(false);

  const toggleLogin = () => {
    setLoginOpen(!isLoginOpen);
    if (isSignUpOpen) setSignUpOpen(false);
  };

  const toggleSignUp = () => {
    setSignUpOpen(!isSignUpOpen);
    if (isLoginOpen) setLoginOpen(false);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-neutral">
      <div className="text-center">
        <h1 className="text-5xl font-bold mb-6">Welcome to Our Platform</h1>
        <p className="mb-6">The ultimate place to manage your tasks efficiently.</p>
        <button className="btn btn-primary mr-4" onClick={toggleLogin}>Login</button>
        <button className="btn btn-accent" onClick={toggleSignUp}>Sign Up</button>
      </div>

      {isLoginOpen && (
        <LoginForm />
      )}

      {isSignUpOpen && (
        <NewUserForm />
      )}
    </div>
  );
};

export default LandingPage;
