import '../styles/App.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ManageContacts from './ManageContacts';
import ManageCompanies from './ManageCompanies';
import ManageToDo from './ManageToDo';

function App() {

  const dispatch = useDispatch();

  useEffect(() => {
    // Dispatch the thunk to fetch companies when the app starts
    // Here you need to determine how you're getting the user's ID.
    // For this example, we'll use a fixed ID, but in a real app,
    // you'd likely get this from the auth state or similar.
    const userId = 1; // Replace this with the actual logic to get the user ID.
    dispatch(fetchCompanies(userId));
  }, [dispatch]);
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Layout />} />
        <Route path="/manage-companies" element={<ManageCompanies />} />
        <Route path="/manage-contacts" element={<ManageContacts />} />
        <Route path="/manage-todo" element={<ManageToDo />} />
      </Routes>
    </Router>
  );
}

