// ListsDisplay.js
import React from 'react';
import { useSelector } from 'react-redux';
import { toggleTodo } from '.';

const ListsDisplay = ({ selectedContactId }) => {
  const contacts = useSelector((state) => state.contacts.contacts);
  const selectedContact = contacts.find(contact => contact.id === selectedContactId);
  const lists = selectedContact ? selectedContact.lists : [];

  return (
    <div className="todo-container">
      {lists.map((list) =>
        list.todos.map((todo) => (
          <div key={todo.id} className={`todo-item ${todo.completed ? 'completed' : ''}`}>
            <div className="custom-checkbox" onClick={() => toggleTodo(todo.id)}>
              {/* If the todo is completed, we add the 'checked' class */}
              {todo.completed && <div className="checkmark" />}
            </div>
            <div className="todo-text">
              {todo.title}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default ListsDisplay;
